import React, { Component } from 'react';

export default class extends Component {


    handleInputChange = (event) => {
        let salary = event.target.value;
        salary = parseFloat(salary).toFixed(2);
        this.setState({
            baseSalary: salary
        });

        // this.calculateINSSDiscount();
    }

    componentDidUpdate() {
        this.calculateINSSDiscount();
        this.calculateIRPFDiscount();

    }

    calculateINSSDiscount() {
        let { baseSalary, discountINSS } = this.state;
        let percentage = 0;
        let discount = 0;
        baseSalary = parseFloat(baseSalary);
        console.log(baseSalary);
        if (baseSalary <= 1045.00) {
            percentage = 0.075;
        }
        else if (baseSalary <= 2089.60) {
            percentage = 0.09;
        }
        else if (baseSalary <= 3134.40) {
            percentage = 0.12;
        }
        else if (baseSalary <= 6101.06) {
            percentage = 0.14;
        }

        discount = baseSalary * percentage;

        if (this.state.discountINSS != discount) {
            this.setState({
                discountINSS: discount,
            });
        }


        console.log(`The inss discount will be ${this.state.discountINSS}`);
    }

    calculateIRPFDiscount() {
        let { baseSalary, discountINSS } = this.state;
        let baseIRPF = baseSalary - discountINSS;
        console.log(`The IRPF base will be ${baseIRPF}`);
        let percentage = 0;
        if (baseIRPF <= 1903.98) {
            percentage = 0;;
        }
        else if (baseIRPF <= 2826.65) {
            percentage = 0.075;
        }
        else if (baseIRPF <= 3751.06) {
            percentage = 0.15;
        }
        else if (baseIRPF <= 4664.68) {
            percentage = 0.225;
        }
        else if (baseIRPF > 4664.68) {
            percentage = 0.275;
        }

        let discountIRPF = baseIRPF * percentage;; //aqui

        if (this.state.discountIRPF != discountIRPF) {
            this.setState({
                baseIRPF,
                discountIRPF,
            });
        }

        console.log(`The IRPF discount will be ${this.state.discountIRPF}`);

    }

    render() {
        return (
            <div>
                <div className="row">
                    <div className="input-field col s12">
                        <input id="base-salary" type="number" className="validate" />
                        <label className="active" >First Name</label>
                    </div>
                </div>

                <div className="row">

                    <div className="row">
                        <div className="input-field col s3">
                            <input id="first_name" type="text" className="validate" disabled value="teste" />
                            <label className="active" > Base Inss</label>
                        </div>
                        <div className="input-field col s3">
                            <input id="last_name" type="text" className="validate" disabled />
                            <label className="active" >Desconto INSS</label>
                        </div>
                        <div className="input-field col s3">
                            <input id="last_name" type="text" className="validate" disabled />
                            <label className="active" >Base IRPF</label>
                        </div>
                        <div className="input-field col s3">
                            <input id="last_name" type="text" className="validate" disabled />
                            <label className="active">Desconto IRPF</label>
                        </div>
                    </div>
                </div>
            </div>


        );
    }
}
