import React, { Component } from 'react';

export default class extends Component {
    // constructor(props) {
    //     super(props);
    //     this.state = {  }
    // }

    componentDidMount() {
        this.setState({
            baseSalary: 0,
            discountINSS: 0,
            baseIRPF: 0,
            discountIRPF: 0,
        });
    }

    handleInputChange = (event) => {
        let salary = event.target.value;
        salary = parseFloat(salary).toFixed(2);
        this.setState({
            baseSalary: salary
        });

        // this.calculateINSSDiscount();
    }

    componentDidUpdate() {
        this.calculateINSSDiscount();
        this.calculateIRPFDiscount();

    }

    calculateINSSDiscount() {
        let { baseSalary, discountINSS } = this.state;
        let percentage = 0;
        let discount = 0;
        baseSalary = parseFloat(baseSalary);
        console.log(baseSalary);
        if (baseSalary <= 1045.00) {
            percentage = 0.075;
        }
        else if (baseSalary <= 2089.60) {
            percentage = 0.09;
        }
        else if (baseSalary <= 3134.40) {
            percentage = 0.12;
        }
        else if (baseSalary <= 6101.06) {
            percentage = 0.14;
        }

        discount = baseSalary * percentage;

        if (this.state.discountINSS != discount) {
            this.setState({
                discountINSS: discount,
            });
        }


        console.log(`The inss discount will be ${this.state.discountINSS}`);
    }

    calculateIRPFDiscount() {
        let { baseSalary, discountINSS } = this.state;
        let baseIRPF = baseSalary - discountINSS;
        console.log(`The IRPF base will be ${baseIRPF}`);
        let percentage = 0;
        if (baseIRPF <= 1903.98) {
            percentage = 0;;
        }
        else if (baseIRPF <= 2826.65) {
            percentage = 0.075;
        }
        else if (baseIRPF <= 3751.06) {
            percentage = 0.15;
        }
        else if (baseIRPF <= 4664.68) {
            percentage = 0.225;
        }
        else if (baseIRPF > 4664.68) {
            percentage = 0.275;
        }

        let discountIRPF = baseIRPF * percentage;; //aqui

        if (this.state.discountIRPF != discountIRPF) {
            this.setState({
                baseIRPF,
                discountIRPF,
            });
        }

        console.log(`The IRPF discount will be ${this.state.discountIRPF}`);

    }


    render() {
        return (
            <div>
                <div class="row">
                    <div className="input-field col s12">
                        <input id="base-salary" type="number" class="validate" />
                        <label className="active" for="first_name2">First Name</label>
                    </div>
                </div>

                <div class="row">

                    <div class="row">
                        <div class="input-field col s6">
                            <input placeholder="Placeholder" id="first_name" type="text" class="validate" />
                            <label for="first_name">First Name</label>
                        </div>
                        <div class="input-field col s6">
                            <input id="last_name" type="text" class="validate" />
                            <label for="last_name">Last Name</label>
                        </div>
                    </div>
                </div>
            </div>


        );
    }
}
