import React, { Component } from 'react';
import css from './readOnlyInput.module.css';


export default class ReadOnlyInput extends Component {
    componentDidMount(){
        console.log(this.props);
    }
    // componentDidUpdate(){
    //     console.log('Read only DISCOUNT updated');
    // }
    render() { 
        const {val, percentage, title} = this.props;
        const perc = percentage * 100;
        const classes = title === 'Desconto INSS:' ? ` ${css.result}  orange-text darken-3` : `${css.result}  red-text darken-3`;
        return ( 
            <div className="input-field col s3">
                <input type="text" className={classes} readOnly value={`${val} (${perc}%)`}/>
                <label className="active" > {title} </label>
            </div>
         );
    }
}
