import React, { Component } from 'react';

export default class extends Component {


    handleInputChange = (event) => {
        let salary = event.target.value;
        salary = parseFloat(salary).toFixed(2);
        this.setState({
            baseSalary: salary
        });

        this.props.onChange();

        // this.calculateINSSDiscount();
    }

    componentDidUpdate(e) {
        console.log(e.target);

    }

    render() {
        return (
            <div>
                <div className="row">
                    <div className="input-field col s12">
                        <input id="base-salary" type="number" className="validate" onChange={this.handleInputChange}/>
                        <label className="active" >First Name</label>
                    </div>
                </div>

                <div className="row">

                    <div className="row">
                        <div className="input-field col s3">
                            <input id="first_name" type="text" className="validate" disabled value="teste" />
                            <label className="active" > Base Inss</label>
                        </div>
                        <div className="input-field col s3">
                            <input id="last_name" type="text" className="validate" disabled />
                            <label className="active" >Desconto INSS</label>
                        </div>
                        <div className="input-field col s3">
                            <input id="last_name" type="text" className="validate" disabled />
                            <label className="active" >Base IRPF</label>
                        </div>
                        <div className="input-field col s3">
                            <input id="last_name" type="text" className="validate" disabled />
                            <label className="active">Desconto IRPF</label>
                        </div>
                    </div>
                </div>
            </div>


        );
    }
}
